CREATE TABLE Staff (StaffID INT NOT NULL, FirstName VARCHAR(24) NOT NULL, LastName VARCHAR(24) NOT NULL, PRIMARY KEY (StaffID))
CREATE TABLE Student (StudentID INT NOT NULL, Title VARCHAR(4), FirstName VARCHAR(30) NOT NULL, LastName VARCHAR(30) NOT NULL, DissTitle VARCHAR(40),Discipline VARCHAR(5), Supervisor1 INT NOT NULL, Supervisor2 INT, Supervisor3 INT, Supervisor4 INT, PRIMARY KEY (StudentID), FOREIGN KEY (Supervisor1) REFERENCES Staff (StaffID), FOREIGN KEY (Supervisor2) REFERENCES Staff (StaffID), FOREIGN KEY (Supervisor3) REFERENCES Staff (StaffID), FOREIGN KEY (Supervisor4) REFERENCES Staff (StaffID))